    else if ($status == "0") { $status_result = "<i class='pe-minus-circle pe-fw'></i> Deleted"; }
  } else {
    if ($status == "2") { $status_result = "Valid"; }
    else if ($status == "1") { $status_result = "Invalid"; }
    else if ($status == "0") { $status_result = "Deleted"; }
  }
  return $status_result;
}

function percent($number) {
  return number_format($number * 100, 1).'%';
}

function percentile($array) {
  $total = array_sum($array);
  $names = array_map(function($number) use ($total) { return number_format($number / $total * 100, 1); }, $array);
  return $names;
}

function isJSON($string) {
  return is_string($string) && is_array(json_decode($string, true)) ? true : false;
}

function backButton($link, $text, $type) {
  if (empty($link)) { $link = "/admin/?w=surveys"; }
  if (empty($text)) { $text = "Back to the main page"; }
  if (empty($type)) { $type = "danger"; }
  echo "<a href=\"$link\" class=\"btn btn-$type btn-sm pull-right\" style=\"margin-top:50px; margin-bottom: 20px\"><i class=\"pe-chevron-circle-left pe-fw\"></i> $text</a>";
}

