function themeBG($colour) {
  if ($colour == 1) { $themeBG = "background-image: url(\"data:image/svg+xml;base64,\")"; }
  else if ($colour == 2) { $themeBG = "background-image: url(\"data:image/svg+xml;base64,\")"; }
  else if ($colour == 3) { $themeBG = "background-image: url(\"data:image/svg+xml;base64,\")"; }
  else if ($colour == 4) { $themeBG = "background-image: url(\"data:image/svg+xml;base64,\")"; }
  else if ($colour == 5) { $themeBG = "background-image: url(\"data:image/svg+xml;base64,\")"; }
  else if ($colour == 6) { $themeBG = "background-image: url(\"data:image/svg+xml;base64,\")"; }
  else if ($colour == 7) { $themeBG = "background-image: url(\"data:image/svg+xml;base64,\")"; }
  else if ($colour == 8) { $themeBG = "background-image: url(\"data:image/svg+xml;base64,\")"; }
  else { $themeBG = "background-image: url(\"data:image/svg+xml;base64,\")"; }
  return $themeBG;
}

